import { defineComponent, getCurrentInstance, ref, computed, watch, onMounted } from 'vue';
import draggable from '../draggable.mjs';
var script = defineComponent({
  name: "ElSlPanel",
  props: {
    color: {
      type: Object,
      required: true
    }
  },
  setup: function setup(props) {
    var instance = getCurrentInstance();
    var cursorTop = ref(0);
    var cursorLeft = ref(0);
    var background = ref("hsl(0, 100%, 50%)");
    var colorValue = computed(function () {
      var hue = props.color.get("hue");
      var value = props.color.get("value");
      return {
        hue: hue,
        value: value
