import { getCurrentInstance, computed } from 'vue';

function useMapState() {
  var instance = getCurrentInstance();
  var table = instance.parent;
  var store = table.store;
  var leftFixedLeafCount = computed(function () {
    return store.states.fixedLeafColumnsLength.value;
  });
  var rightFixedLeafCount = computed(function () {
    return store.states.rightFixedColumns.value.length;
  });
  var columnsCount = computed(function () {
    return store.states.columns.value.length;
  });
  var leftFixedCount = computed(function () {
    return store.states.fixedColumns.value.length;
  });
  var rightFixedCount = computed(function () {
    return store.states.rightFixedColumns.value.length;
  });
  return {
    leftFixedLeafCount: leftFixedLeafCount,
    rightFixedLeafCount: rightFixedLeafCount,
    columnsCount: columnsCount,
    leftFixedCount: leftFixedCount,
    rightFixedCount: rightFixedCount,
    columns: store.states.columns
  };
}

export { useMapState as default };
